// src/App.js
import React, { Component } from 'react';
import PhoneForm from './components/PhoneForm';
import PhoneInfoList from './components/PhoneInfoList';

class App extends Component {
  id = 2
  state={
    information: [
    {
      id: 0,
      name: '김민준',
      phone: '010-0000-0000'
    },
    {
      id:1,
      name: '홍길동',
      phone: '010-0000-0001'
    }
  ],
  keyword: ''
  }
  handleChange = (e) => {
    this.setState({
      keyword: e.target.value,
    });
  }
  handleCreate = (data) =>{
    const {information} =this.state;
    this.setState({
      information: information.concat({id: this.id++, ...data})
    })
  }
  handleRemove = (id) => {
    const {information} = this.state;
    this.setState({
      information: information.filter(info=>info.id !== id)
    })
  }
  handleUpdate = (id,data)=>{
    const {information} = this.state;
    this.setState({
      information: information.map(
        info => id === info.id?
        {...info, ...data} // 새 객체를 만들어서 기존의 값과 전달받은 data를 덮어씀
        : info // 기존의 값을 그대로 유지
      )
    })
  }

  render() {
    const { information, keyword}=this.state;
    const filteredList = information.filter(
      info => info.name.indexOf(keyword) !== -1
    );
    return (



    <div class="container auth">
        <a class="logo">MEMOPAD</a>
        <div class="card">
            <div class="header blue white-text center">
                <div class="card-content">LOGIN</div>
            </div>
            <div class="card-content">
                <div class="row">

                    <div class="input-field col s12 username">
                        <label>Username</label>
                        <input
                        name="username"
                        type="text"
                        class="validate"/>
                    </div>
                    <div class="input-field col s12">
                        <label>Password</label>
                        <input
                        name="password"
                        type="password"
                        class="validate"/>
                    </div>
                    <a class="waves-effect waves-light btn">SUBMIT</a>
                </div>
            </div>


            <div class="footer">
                <div class="card-content">
                    <div class="right" >
                        New Here? <a>Create an account</a>
                    </div>
                </div>
            </div>
        </div>
    </div>

    );
  }

}

export default App;
